import actionTypes from '../actions/action-types'

const initialState = {}

export default function productsReducer (action, state = initialState) {
  switch (action.type) {
    case actionTypes.LOAD_PRODUCTS:
      return {
        ...state,
        productsList: action.productsList
      }
    default:
      return state
  }
}
