import React, { useEffect } from 'react';
import {
  Text, View, FlatList, Image,
} from 'react-native';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Icon from 'react-native-vector-icons/AntDesign';
import { useNavigation, CommonActions } from '@react-navigation/native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { loadOrderProductsList } from '../../redux/actions/productsActions';
import styles from './OrderStyles';

function Order({ orderList, dispatch }) {
  const navigation = useNavigation();

  useEffect(() => {
    if (!orderList || !orderList.length) {
      dispatch(loadOrderProductsList());
    }
  },
  []);

  return (
    orderList.length !== 0
    && (
    <FlatList
      ListHeaderComponent={(
        <View style={styles.titleContainer}>
          <TouchableOpacity
            onPress={() => navigation.dispatch(CommonActions.goBack())}
          >
            <Icon
              name="close"
              size={32}
            />
          </TouchableOpacity>
          <Text style={styles.title}>Mi Pedido</Text>
        </View>
    )}
      data={orderList}
      horizontal={false}
      keyExtractor={(item) => item.product.name}
      numColumns={2}
      renderItem={({ item }) => (
        <View style={styles.productView}>
          <View style={styles.imageContainer}>
            <Image
              source={{ uri: item.product.img }}
              style={styles.image}
            />
          </View>
          <Text style={styles.productTitle}>{item.product.name}</Text>
          <Text style={styles.price}>{`${item.product.price.toFixed(2)} €`}</Text>
          <View style={styles.buttons}>
            <TouchableOpacity
              style={styles.button}
            >
              <Icon
                color="#FFF"
                name="minus"
                size={30}
              />
            </TouchableOpacity>
            <Text style={styles.quantity}>{item.quantity}</Text>
            <TouchableOpacity
              style={styles.button}
            >
              <Icon
                color="#FFF"
                name="plus"
                size={30}
              />
            </TouchableOpacity>
          </View>
        </View>
      )}
    />
    )
  );
}

Order.propTypes = {
  dispatch: PropTypes.func.isRequired,
  orderList: PropTypes.arrayOf(PropTypes.object),
};

Order.defaultProps = {
  orderList: [],
};

function mapStateToProps({ orderReducer }) {
  return {
    orderList: orderReducer.orderList,
  };
}

export default connect(mapStateToProps)(Order);
